%spark
var local = Array(1, 2, 3, 4, 5);
{
    val arr = sc.parallelize(List(1, 2, 3, 4, 5));
    val result = arr.map(x => x * x);
    local = result.collect();
}
print(local.zip(Array(1, 4, 9, 16, 25)).map{ case (x, y) => x == y}.reduceLeft(_ && _))

%spark2
var local = Array(1L, 2L, 3L, 4L, 5L);
{
    val arr = spark.range(1, 5);
    val result = arr.withColumn("id", arr("id") * arr("id"))
    local = result.select("id").rdd.collect().map(x => x.getLong(0));
}
print(local.zip(Array(1, 4, 9, 16, 25)).map{ case (x, y) => x == y}.reduceLeft(_ && _))
